#!swipl -q
:-initialization input(X), solution(X, S), writeln(S), halt(0).

diff_list([H1|T1], [H2|T2], S) :- diff(H1, H2, Z), diff_list(T1, T2, Q), S is Z+Q.
diff_list([], [], 0).
diff(A,B,S) :- A > B, S is A-B.
diff(A,B,S) :- S is B-A.

merged([H1|T1],[H2|T2],[H1|Z]) :- H1 < H2, merged(T1,[H2|T2],Z).
merged(L1,[H2|T2],[H2|Z]) :- merged(L1,T2,Z).
merged([], L, L).
merged(L, [], L).

sort_two([H1,H2|[]], [H1,H2|[]]) :- H1 < H2.
sort_two([H1,H2|[]], [H2,H1|[]]).
sort_two([H1|[]], [H1]).

doubles([H1, H2|T], [[H1, H2]|S2]) :- doubles(T, S2).
doubles([],[]).
doubles([H1|[]], [[H1]]).
sorted_doubles([H|T], [HS|TS]) :- sort_two(H, HS), sorted_doubles(T, TS).
sorted_doubles([], []).

merge_all([H1,H2|T], S) :- merged(H1, H2, S1), merge_all(T, S2), merged(S1, S2, S).
merge_all([H1,H2|[]], S) :- merged(H1, H2, S).
merge_all([H1|[]], H1).
merge_all([], []).

sort_list(A, S) :- doubles(A, L), sorted_doubles(L, LS), merge_all(LS, S).

solution([L1, L2], S) :- sort_list(L1, LS1), sort_list(L2, LS2), diff_list(LS1, LS2, S).

